CREATE DATABASE vk_bot;
CREATE TABLE `vk_bot`.`USERS` ( `id_user` INT NOT NULL AUTO_INCREMENT , `uid` INT NOT NULL , `user_name` TEXT NOT NULL , `birthday` TEXT NOT NULL , PRIMARY KEY (`id_user`)) ENGINE = InnoDB;
CREATE TABLE `vk_bot`.`GOODS` ( `id_good` INT NOT NULL AUTO_INCREMENT ,  `good_name` TEXT NOT NULL ,  `good_description` TEXT NOT NULL ,  `good_price` INT NOT NULL ,  `sale_from` INT NOT NULL ,  `ordered` INT NOT NULL ,  `remain` INT NOT NULL , `photo_id` TEXT NOT NULL ,   PRIMARY KEY  (`id_good`)) ENGINE = InnoDB;
CREATE TABLE `vk_bot`.`ORDERS` ( `id_order` INT NOT NULL AUTO_INCREMENT ,  `id_user` INT NOT NULL ,  `id_good` INT NOT NULL ,  `order_price` INT NOT NULL ,  `status` INT NOT NULL ,  `quantity` INT NOT NULL ,  `total_price` INT NOT NULL ,  `order_hash` TEXT NOT NULL ,  `order_time` TEXT NOT NULL ,  `sent_order` TEXT NULL ,  `order_get` TEXT NULL ,  `payment_hash` TEXT NOT NULL ,    PRIMARY KEY  (`id_order`)) ENGINE = InnoDB;
CREATE TABLE `vk_bot`.`PAYMENTS` ( `id_payment` INT NOT NULL AUTO_INCREMENT , `id_user` INT NOT NULL , `payment_hash` TEXT NOT NULL , `order_hash` TEXT NOT NULL , `time_made` TEXT NOT NULL , `time_payed` TEXT NULL , PRIMARY KEY (`id_payment`)) ENGINE = InnoDB;
